<?PHPob_start();session_start();require_once("inc/config/pdoconnect.php");require_once("inc/config/pdohandler.php");require_once("inc/config/pdofunctions.php");require_once("inc/config/pdodefinitions.php");$conn = new dbconnect();$dbconn= new dbhandler();$converter = new Encryption;header('Content-Type: text/csv');header('Content-Disposition: attachment;filename='.$_SESSION['_exp_file'].'.csv');try{	if($_SESSION['_exp_qery'] != ""){			$fp = fopen('php://output', 'w');		$slNo = 0;			$stmt = $conn->prepare($_SESSION['_exp_qery']);			$stmt->execute();			$first_row = $stmt->fetch(PDO::FETCH_ASSOC);			$headers = array_keys($first_row);			fputcsv($fp, $headers); // put the headers			fputcsv($fp, array_values($first_row)); // put the first row			while ($row = $stmt->fetch(PDO::FETCH_NUM))  {				fputcsv($fp,$row); // push the rest			}			$row = null;			$stmt = null;		fclose($fp);	}	unset($_SESSION['_exp_file']);	unset($_SESSION["_exp_qery"]);}catch (PDOException $e) {	echo $_SESSION['_msg']="Error!: ".$e->getMessage()."";					//header("location:home.php");	die();	}exit;?>