<?phpisset($_REQUEST['iResultsLimit']) ? : $_REQUEST['iResultsLimit'] = 25;isset($_REQUEST['iPageNum']) ? $iPageNum = $_REQUEST['iPageNum'] : $iPageNum = 1;function show_records($iResultsLimit=10) {	return '<div class="form-group row m-b-5">						<label for="example-text-input" class="col-lg-8 col-md-6 hidden-xs hidden-sm small text-right col-form-label">Show<br>Records</label>						<div class="col-lg-4 col-sm-12 col-md-12 col-xs-12">							<select id="iResultsLimit" name="iResultsLimit" class="selectpicker pull-right cis_filter" data-style="btn-info btn-outline">								<option value="5">5</option>								<option value="10">10</option>								<option value="25">25</option>								<option value="50">50</option>							</select><script>document.getElementById("iResultsLimit").value="'.$iResultsLimit.'";</script>						</div>					</div>';}function do_pages_cis($total,$page_size=25,$current=1) {			$index_limit = 5;	$show_cnt=1;	$query = preg_replace('/page=[0-9]+/', '', $_SERVER['QUERY_STRING']);	$query = preg_replace('/^&*(.*)&*$/', "$1", $query);	if (!empty($query)) $query = "&amp;$query";	//$current = GetVar("iPageNum");	if (intval($current) <= 0)	$current = 1;	$total_pages = ceil($total / $page_size);	$start = max($current - intval($index_limit / 2), 1);	$end = $start + $index_limit - 1;				if ($start>1) {		$i = 1;	}	$showing_start = ((($current-1)*$page_size)+1);	$showing_end = $current*$page_size;	if($showing_end >= $total){		$showing_end = $total;	}						echo '<div class="row">';	if($show_cnt == 1){		echo '<div class="col-sm-6"><div class="dataTables_info" id="editable-datatable_info" role="status" aria-live="polite">Showing '.$showing_start.' to '.$showing_end.' of '.$total.' entries</div></div>';	}	echo '<div class="col-sm-6"><div class="dataTables_paginate paging_simple_numbers" id="editable-datatable_paginate"><ul class="pagination">';	if ($total > 0) {		if ($current == 1) {							echo '<li class="paginate_button previous disabled" aria-controls="editable-datatable" id="editable-datatable_previous"><a href="javascript:;">Previous</a></li>';		} else {			$i = $current - 1;			echo '<li class="paginate_button previous" aria-controls="editable-datatable" id="editable-datatable_previous"><a class="cis_pages" rel="'.$i.'" href="javascript:;">Previous</a></li>';		}	}	for ($i = $start; $i <= $end && $i <= $total_pages; $i++) {		if ($i == $current) {			echo '<li class="paginate_button active" aria-controls="editable-datatable"><a href="javascript:;">'.$i.'</a></li>';		} else {			if(($i == $end) || ($i == $total_pages)) {			echo '<li class="paginate_button" aria-controls="editable-datatable"><a class="cis_pages" rel="'.$i.'" href="javascript:;">'.$i.'</a></li>';			}else{			echo '<li class="paginate_button" aria-controls="editable-datatable"><a class="cis_pages" rel="'.$i.'" href="javascript:;">'.$i.'</a></li>';			}		}	}	if ($total_pages > $end) {		$i = $total_pages;		//echo ' . . . ';		echo '<li class="paginate_button" aria-controls="editable-datatable"><a href="javascript:;">...</a></li>';		echo '<li class="paginate_button" aria-controls="editable-datatable"><a class="cis_pages" rel="'.$i.'" href="javascript:;">'.$i.'</a></li>';	}	if ($total > 0) {		if ($current < $total_pages) {			$i = $current + 1;							echo '<li class="paginate_button next" aria-controls="editable-datatable" id="editable-datatable_next"><a class="cis_pages" rel="'.$i.'" href="javascript:;">Next</a></li>';		} else {			echo '<li class="paginate_button next disabled" aria-controls="editable-datatable" id="editable-datatable_next"><a href="javascript:;">Next</a></li>';		}	}		echo '</ul></div></div>';		echo '</div>';			}?>